    <script>
        // Fungsi untuk membuka modal
        function openModal(id) {
            console.log(`Membuka modal: ${id}`);
            const modal = document.getElementById(id);
            if (modal) {
                modal.style.display = 'flex';
                document.body.style.overflow = "hidden"; 
            } else {
                console.error(`Modal dengan ID ${id} tidak ditemukan.`);
            }
        }

        // Fungsi untuk menutup modal
        function closeModal(id) {
            console.log(`Menutup modal: ${id}`);
            const modal = document.getElementById(id);
            if (modal) {
                modal.style.display = 'none';
                document.body.style.overflow = "auto";
            }
        }

        // Menutup modal ketika area di luar modal ditekan
        document.addEventListener('click', function(event) {
            const modals = document.querySelectorAll('[id^="detailModal-"]');
            modals.forEach(modal => {
                if (event.target === modal) {
                    modal.style.display = 'none';
                }
            });
        });
    </script>
